<?php	class Model_Portfolio extends Model{		public function get_data(){				//пример соединения с MySQL при помощи PDO			$array_model = array();			$db = new PDO('mysql:host='.DB_HOST.';dbname='.DB_NAME, DB_USER, DB_PASS);			$rezult = $db->query('SELECT * from admin');			$rows = $rezult->fetchAll();			$count = count($rows);			foreach($rows as $row){				return array($row);			}		}	}